

const loadProducts = async () => {
    const response = await fetch(`${url}/api/products`, {
        method: 'GET'
    });
    const jsonParsedResponse = await response.json();
    const products = jsonParsedResponse.payload;
    let productContainer = document.getElementById("product-list");

    products.forEach(function (product) {
        let card = document.createElement("div");
        card.classList.add("card", "productCardCustom", "col-md-4", "custom"); 

        let imageUrl = "";

        if (product.thumbnails.length > 0) {
            if (product.thumbnails[0].length > 0) {
                imageUrl = product.thumbnails[0];
            } else {
                imageUrl = "data:image/jpeg;base64,";
            }
        }
        else {
            imageUrl = "data:image/jpeg;base64,";
        }



        let cardContent = `        
                <img src="${imageUrl}" class="card-img-top productCardImage" alt="${product.title}">
                <div class="card-body">
                    <h5 class="card-title ">${product.title}</h5>
                    <p class="card-text">Stock: ${product.stock}</p>
                    <p class="card-text">${product.description}</p>
                    <p class="card-text">Price: $${product.price}</p>
                    <button class="btn btn-primary add-to-cart" id="btn${product._id}">AÃ±adir al carrito</button>
                </div>        
        `;
        card.innerHTML = cardContent;
        productContainer.appendChild(card);
        document.getElementById(`btn${product._id}`).addEventListener("click", async (e) => {
            e.preventDefault();
            const addToCartResponse = await fetch(`${url}/api/carts/product/${product._id}`, {
                method: 'POST'
            });
            const jsonParsedResponse = await addToCartResponse.json();
            console.log(jsonParsedResponse)
            if (addToCartResponse.status === 401) {
                return Swal.fire({
                    position: 'top-end',
                    icon: 'error',
                    title: `Error: ${jsonParsedResponse.message}`,
                    showConfirmButton: false,
                    timer: 1500
                }).then(() => {
                    window.location.href = `${url}/handlebars/login`;
                })
            }
            if (!addToCartResponse.ok) {
                return Swal.fire({
                    position: 'top-end',
                    icon: 'error',
                    title: `Error: ${jsonParsedResponse.message}`,
                    showConfirmButton: false,
                    timer: 1500
                })
            }
            return Swal.fire({
                position: 'top-end',
                icon: 'success',
                title: `${jsonParsedResponse.message}`,
                showConfirmButton: false,
                timer: 1500
            })
        })
    });
};



loadProducts();
